    timer_4 = threading.Timer(86400, send_but_three, args=[chat_id, message_id])
    timer_5 = threading.Timer(160000, send_reminders, args=[chat_id])
    active_timers[chat_id] = [timer_1, timer_2, timer_4]  # –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω—ã—Ö —Ç–∞–π–º–µ—Ä–æ–≤ –¥–ª—è –¥–∞–Ω–Ω–æ–≥–æ —á–∞—Ç–∞
    timer_1.start()
    timer_2.start()
    timer_4.start()
    timer_5.start()
